<script>
    // Ensure GSAP + ScrollTrigger are available (CDN fallback)
    function ensureGsap(callback) {
        if (window.gsap && window.ScrollTrigger) {
            return callback();
        }

        const loadScript = (src) => new Promise((resolve, reject) => {
            const s = document.createElement('script');
            s.src = src;
            s.async = true;
            s.onload = resolve;
            s.onerror = reject;
            document.head.appendChild(s);
        });

        (async () => {
            try {
                if (!window.gsap) {
                    await loadScript('https://cdn.jsdelivr.net/npm/gsap@3.12.2/dist/gsap.min.js');
                }
                if (!window.ScrollTrigger) {
                    await loadScript('https://cdn.jsdelivr.net/npm/gsap@3.12.2/dist/ScrollTrigger.min.js');
                }
            } catch (e) {
                console.warn('Failed to load GSAP from CDN:', e);
            } finally {
                callback();
            }
        })();
    }

    function initGsap() {
        ensureGsap(() => {
            try {
                gsap.registerPlugin(ScrollTrigger);

                // Kill old triggers (important for SPA / Livewire)
                ScrollTrigger.getAll().forEach(t => t.kill());
                gsap.utils.toArray(".gsap-fade-left").forEach((el) => {
                    gsap.fromTo(el, {
                        opacity: 0,
                        x: -80
                    }, {
                        opacity: 1,
                        x: 0,
                        duration: 1,
                        ease: "power3.out",
                        scrollTrigger: {
                            trigger: el,
                            start: "top 85%",
                            end: "bottom 20%",
                            toggleActions: "play reverse play reverse"
                        }
                    });
                });

                // ==============================
                // Fade Right
                // ==============================
                gsap.utils.toArray(".gsap-fade-right").forEach(el => {
                    gsap.fromTo(el, {
                        x: 80,
                        opacity: 0
                    }, {
                        x: 0,
                        opacity: 1,
                        duration: 1.1,
                        ease: "power3.out",
                        scrollTrigger: {
                            trigger: el,
                            start: "top 85%",
                            end: "bottom 20%",
                            toggleActions: "play reverse play reverse"
                        }
                    });
                });

                // ==============================
                // Fade Up
                // ==============================
                gsap.utils.toArray(".gsap-fade-up").forEach(el => {
                    gsap.fromTo(el, {
                        y: 60,
                        opacity: 0
                    }, {
                        y: 0,
                        opacity: 1,
                        duration: 1,
                        ease: "power3.out",
                        scrollTrigger: {
                            trigger: el,
                            start: "top 85%",
                            end: "bottom 20%",
                            toggleActions: "play reverse play reverse"
                        }
                    });
                });
                gsap.utils.toArray(".gsap-fade-down").forEach(el => {
                    gsap.fromTo(el, {
                        y: -60,
                        opacity: 0
                    }, {
                        y: 0,
                        opacity: 1,
                        duration: 1,
                        ease: "power3.out",
                        scrollTrigger: {
                            trigger: el,
                            start: "top 85%",
                            end: "bottom 20%",
                            toggleActions: "play reverse play reverse"
                        }
                    });
                });

                // ==============================
                // Stagger Blog List
                // ==============================
                gsap.utils.toArray(".blog-list").forEach(list => {
                    const items = list.querySelectorAll(".blog-item");

                    gsap.fromTo(items, {
                        y: 50,
                        opacity: 0
                    }, {
                        y: 0,
                        opacity: 1,
                        duration: 0.8,
                        ease: "power3.out",
                        stagger: 0.15,
                        scrollTrigger: {
                            trigger: list,
                            start: "top 80%",
                        }
                    });
                });

                // ==============================
                // Parallax Image
                // ==============================
                gsap.utils.toArray(".gsap-parallax").forEach(img => {
                    gsap.to(img, {
                        y: -80,
                        ease: "none",
                        scrollTrigger: {
                            trigger: img,
                            start: "top bottom",
                            end: "bottom top",
                            scrub: true
                        }
                    });
                });

            } catch (err) {
                console.error('GSAP init error:', err);
            }
        });
    }

    // ==============================
    // Run on load
    // ==============================
    document.addEventListener("DOMContentLoaded", () => {
        initGsap();
    });

    // ==============================
    // If using Livewire / SPA / Turbo
    // ==============================
    document.addEventListener("livewire:navigated", () => {
        setTimeout(() => initGsap(), 50);
    });

    document.addEventListener("turbo:load", () => {
        setTimeout(() => initGsap(), 50);
    });

    // ==============================
    // Fallback: run immediately if DOM is already loaded
    // ==============================
    if (document.readyState === 'loading') {
        // DOM not ready, wait for DOMContentLoaded
    } else {
        // DOM already loaded, run now
        initGsap();
    }
